INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBDE1A01', 'Diversidad alfa', 'Mide la diversidad de especies dentro de un habitat especifico en un momento dado.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBDE1A02', 'Numero de especies', 'Representa la cantidad total de especies presentes en un area o habitat.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBDE1A03', 'Numero de especies endemicas', 'Indica la cantidad de especies que son exclusivas de un area geografica especifica.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBDE1A04', 'Numero de especies amenazadas', 'Cuantifica la cantidad de especies que se encuentran en riesgo de extincion o estan en peligro.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBDE1A05', 'Numero de especies invasoras', 'Refleja la presencia de especies exoticas que han sido introducidas en un area y que pueden tener impactos negativos en el ecosistema local.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBDE1A06', 'Margalef', 'Estima la riqueza de especies en relacion con el numero total de individuos presentes.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBDE1A07', 'Indice de rareza', 'Evaluacion de la singularidad y escasez de especies raras o unicas presentes en un area.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBDE1A08', 'Vulnerabilidad de especies', 'Estimacion del riesgo de extincion de una especie en funcion de su biologia, historia natural y amenazas existentes.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBDE1A09', 'Diversidad Genetica', 'Evalua la diversidad genetica dentro de una poblacion o comunidad, lo que puede influir en su capacidad de adaptacion y supervivencia a largo plazo.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBDE1A10', 'Resistencia Genetica', 'Este indice evalua la capacidad de una poblacion o comunidad para resistir enfermedades, plagas u otros agentes estresantes bioticos mediante la presencia de diversidad genetica dentro de la poblacion.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBAD1B01', 'Berger-Parker', 'Indica la proporcion de la abundancia total representada por la especie mas abundante en un conjunto de datos.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBAD1B02', 'Fragmentacion del Habitat', 'Medida de la division y aislamiento de habitats dentro del area protegida.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBAD1B03', 'Fragmentacion del Paisaje', 'Este indice se enfoca en evaluar como la fragmentacion del paisaje afecta la conectividad y la dispersion de especies.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBAD1B04', 'Conectividad ecologica', 'Evalua la facilidad con la que las especies pueden moverse entre diferentes habitats dentro del area protegida');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBAD1B05', 'Diversidad Funcional', 'Este indice considera la variedad de funciones ecologicas que desempenyan las especies dentro de un ecosistema.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C01', 'Salud del habitat', 'Evaluacion del estado general del habitat en el area protegida.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C02', 'Calidad del agua', 'Evalua la pureza y el estado general del agua en un ecosistema, considerando parametros fisicos, quimicos y biologicos.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C03', 'Calidad del aire', 'Cuantifica la pureza y el estado general del aire en un area determinada, teniendo en cuenta contaminantes y otros factores relevantes.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C04', 'Calidad del suelo', 'Indica la fertilidad, estructura y salud general del suelo en un ecosistema, considerando propiedades fisicas, quimicas y biologicas.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C05', 'Erosion de suelos', 'Evaluacion del grado de erosion de suelos dentro del area protegida.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C06', 'Impacto de cambio climatico', 'Evaluacion del impacto del cambio climatico en la biodiversidad del area protegida.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C07', 'Resiliencia ecologica', 'Medida de la capacidad de un ecosistema para recuperarse despues de sufrir una perturbacion.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C08', 'Resiliencia frente a Especies Invasoras', 'Evalua la capacidad de un ecosistema para resistir y recuperarse de la invasion de especies exoticas, considerando la resistencia y la capacidad de recuperacion de las comunidades nativas.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C09', 'Resistencia a perturbaciones', 'Medida de la capacidad de un ecosistema para mantener su estructura y funciones ante perturbaciones externas.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C10', 'Vulnerabilidad del ecosistema', 'Cuantificacion del riesgo de danyo o perdida de un ecosistema debido a amenazas internas o externas.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C11', 'Indice de Salud de los Bosques', 'Evalua la condicion general de un bosque en terminos de la estructura del dosel, la diversidad de especies, la regeneracion y la presencia de enfermedades o plagas.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBSA1C12', 'Vulnerabilidad Climatica', 'Cuantifica la susceptibilidad de un ecosistema a los efectos del cambio climatico, considerando factores como la exposicion, la sensibilidad y la capacidad adaptativa.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBVE1D01', 'Cobertura vegetal', 'Evaluacion cuantitativa de la extension y densidad de la vegetacion en un area determinada.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBVE1D02', 'Eficiencia trofica', 'Evaluacion de la transferencia de energia entre diferentes niveles troficos en un ecosistema.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBVE1D03', 'Biomasa', 'Mide la cantidad total de materia organica producida por plantas y otros organismos en un area especifica.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBVE1D04', 'Numero de especies vegetales', 'Contar el numero de especies vegetales presentes en un area especifica tambien es un indicador importante de la biodiversidad vegetal.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBPC1E01', 'Estado de las Poblaciones', 'Evaluacion del estado de las poblaciones de especies en riesgo.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBPC1E02', 'Integridad Ecologica', 'Indica la salud general del ecosistema en funcion de la composicion y abundancia de especies presentes.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBPC1E03', 'Exito reproductiva', 'Medida de la eficacia de la reproduccion y la crianza de individuos o poblaciones en un area especifica.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBPC1E04', 'Mortalidad', 'Cuantificacion de la tasa de mortalidad de individuos o poblaciones en un area durante un periodo de tiempo especifico.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBPC1E05', 'Habitat optimo', 'Evalua la calidad y disponibilidad de habitats adecuados para especies especificas en un area determinada.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBPC1E06', 'Habitat critico', 'Indica la proporcion de habitats criticos necesarios para mantener poblaciones viables de especies en riesgo de extincion.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBPC1E07', 'Integridad de Humedales', 'Evalua la salud y funcionalidad de los humedales dentro del area protegida.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBEC1F01', 'Shannon-Wiener', 'Cuantifica la incertidumbre asociada con la identificacion de una especie aleatoria de un conjunto de especies presentes.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBEC1F02', 'Simpson', 'Mide la probabilidad de que dos individuos seleccionados al azar en una muestra pertenezcan a la misma especie.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBEC1F03', 'Biomagnificacion', 'Medida de la acumulacion de contaminantes a lo largo de una cadena alimentaria en un ecosistema.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBEC1F04', 'Adaptabilidad', 'Evaluacion de la capacidad de una especie o comunidad para ajustarse a cambios en el entorno.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBEC1F05', 'Especializacion', 'Evalua la especializacion de las especies en terminos de su dependencia de recursos especificos dentro de un ecosistema.');
INSERT INTO INDICADOR_BIODIVERSIDAD(ID, NOMBRE, DESCRIPCION) VALUES('IBEC1F06', 'Invasion Biologica', 'Cuantifica la presencia y el impacto de especies invasoras en un ecosistema, considerando factores como la abundancia, la distribucion y los efectos negativos en la biodiversidad nativa.');
